import React from 'react';
import Board from './Board';
const size=10;
const nsquare=5;
class Game extends React.Component {
    constructor(){
        super();
        this.state = {
          history: [{
            squares: Array(100).fill(null),
            moveLocation: '', 
          }],
          xIsNext: true,
          stepNumber: 0,
          isReverse: false,
        };
      }
      changeReverse(isReverse){
        this.setState({
          isReverse: !isReverse
        });
      }
      handleClick(i){
        const history = this.state.history.slice(0, this.state.stepNumber + 1);
        const current = history[history.length - 1];
        const squares = current.squares.slice();
        if(calculateWinner(squares) || squares[i]){
          return;
        }
    
        const matrixSize = Math.sqrt(history[0].squares.length);
        const moveLocation = [Math.floor(i / matrixSize) + 1, (i % matrixSize) + 1].join(", "); 
        squares[i] = this.state.xIsNext ? 'X' : 'O';
        this.setState({
          history: history.concat([{
            squares: squares,
            moveLocation: moveLocation,
          }]),
          xIsNext: !this.state.xIsNext,
          stepNumber: history.length
        });
      }
    jumpTo(step) {
      this.setState({
        stepNumber: step,
        xIsNext: (step % 2) === 0
      });
    }
  
    render() {
      const history = this.state.history;
      const current = history[this.state.stepNumber];
      const winner = calculateWinner(current.squares);
      const squares = current.squares;  
      const isReverse = this.state.isReverse;  
  
      let status;
      if(winner){
        status = "Winner is: " + winner.winnerPlayer;;
      }else if(this.state.stepNumber === 100){ 
        status = "Draw"; 
      }else{
        status = "Next player is: " + (this.state.xIsNext ? 'X' : 'O');
      }
      const moves = history.map((step, move) => {
        const description = move ? `Move #${move} (${step.moveLocation})` : 'Game start'; 
       return ( <li key={move}>
        <button onClick={() => this.jumpTo(move)}>{description}</button>
      </li>)
      });
      return(
        <div>
          <div className="game">
            <Board squares={squares} onClick={i => this.handleClick(i)} winner={winner && winner.winnerLocation}/>
          </div>
          <div className="game-info">
            <p>{status}</p>
            <ol reversed={isReverse ? 'reverse' :''}>{isReverse ? moves.reverse() : moves}</ol>
            <button onClick={() => this.changeReverse(isReverse)}>Reverse list</button>
          </div>
        </div>
      );
    }
  }

export default Game;

function calculateWinner(squares) {
  let win;
  for (let i = 0; i < size; i++) {
    for (let j = 0; j < size; j++) {
      if (!squares[i*size+j]) continue;
      if (j <= size - 5) {
        win = true;
        for (let k = 0; k < nsquare - 1; k++) {
          if (squares[i*size+j + k] !== squares[i*size+j + k + 1]) {
            win = false
          }
        }
        if (win) return {winnerPlayer: squares[i*size+j],
          winnerLocation: [i*size+j,(i+1)*size+j,i*size+j,(i+2)*size+j,i*size+j,(i+3)*size+j,i*size+j,(i+4)*size+j]};
      }
    }
  }
      
  return null;
    
  }
